import java.io.*;
import java.util.*;

class FileTest_1{
	public static void main(String args[])throws Exception{
		student_info obj = new student_info("Yash Shinde", 21, "Kolhapur", 80, 5.8);
		writeToFile(obj);
		readFromFile();
	}
	public static void  writeToFile(student_info obj1)throws IOException
	{	
		FileOutputStream fs = new FileOutputStream("abc.txt");
		ObjectOutputStream oos = new ObjectOutputStream(fs);		
		oos.writeObject(obj1);
	}
	public static void  readFromFile()throws ClassNotFoundException, IOException
	{
		FileInputStream fi = new FileInputStream("abc.txt");
		ObjectInputStream ois = new ObjectInputStream(fi);
		student_info obj2 = (student_info) ois.readObject();
		System.out.println("Name: "+obj2.name+"\n"+"Age: "+obj2.age+"\n"+"City: "+obj2.city+"\n"+"Weight: "+obj2.weight+"\n"+"Height: "+obj2.height+"\n");
	}
}
class student_info implements Serializable{
	String name,city;
	int age,weight;
	double height;

	student_info(String name, int age,String city,int weight,double height)
	{
		this.name = name;
		this.age = age;
		this.city = city;
		this.weight = weight;
		this.height = height;
	}
}

import java.util.*;
import java.io.*;
class FileTest_2{
	public static void main(String args[])throws Exception{
		String s = " ";
		File f = new File(args[0]);
		FileReader fr = new FileReader(f);
		BufferedReader br = new BufferedReader(fr);
		LinkedList<String> list= new LinkedList<String>();
		while((s = br.readLine()) != null)
		{
			list.add(s);
		}
		Iterator i = list.descendingIterator();
		while(i.hasNext()){
			System.out.println(i.next());
		}
	}
}
